```c++
#include <stdio.h>
#include <vector>
#include <string>
using namespace std;

// Function to check if the combined strings form a good string (balanced parentheses)
string match_parens(vector<string> lst) {
    // The two strings to be combined
    string first = lst[0];
    string second = lst[1];

    // Function to check if a string of parentheses is balanced
    auto is_balanced = [](const string& s) {
        int balance = 0; // Balance counter for parentheses
        for (char c : s) {
            if (c == '(') {
                balance++; // Increment balance for every '('
            } else if (c == ')') {
                balance--; // Decrement balance for every ')'
            }
            // If balance goes negative, there are more ')' than '(' at some point
            if (balance < 0) {
                return false; // Not balanced
            }
        }
        return balance == 0; // Balanced if balance is zero at the end
    };

    // Check both combinations: first + second and second + first
    if (is_balanced(first + second) || is_balanced(second + first)) {
        return "Yes"; // Good string can be formed
    } else {
        return "No"; // Good string cannot be formed
    }
}

int main() {
