import java.time.Year;

/**
 * Custom exception class for handling Car-related validation errors
 * Extends standard Exception class to provide specific error handling
 */
class CarException extends Exception {
    /**
     * Constructor with custom error message
     * @param message Detailed error description
     */
    public CarException(String message) {
        super(message);
    }
}

/**
 * Car class representing a vehicle with various attributes and methods
 * Implements basic car functionality with error handling
 */
class Car {
    // Car attributes with private access for encapsulation
    private String make;
    private String model;
    private int year;
    private double fuelEfficiency;
    private double distanceTravelled;
    private double currentSpeed;
    private double fuelLevel;

    /**
     * Constructor to initialize Car object with validation
     * @param make Car manufacturer
     * @param model Car model name
     * @param year Manufacturing year
     * @param fuelEfficiency Fuel consumption rate
     * @throws CarException If validation fails
     */
    public Car(String make, String model, int year, double fuelEfficiency) throws CarException {
        // Validate year
        if (year < 1900 || year > Year.now().getValue()) {
            throw new CarException("Invalid year: Must be between 1900 and current year");
        }

        // Validate fuel efficiency
        if (fuelEfficiency <= 0) {
            throw new CarException("Fuel efficiency must be a positive value");
        }

        this.make = make;
        this.model = model;
        this.year = year;
        this.fuelEfficiency = fuelEfficiency;
        this.distanceTravelled = 0;
        this.currentSpeed = 0;
        this.fuelLevel = 0;
    }

    /**
     * Accelerate the car by increasing speed
     * @param speedIncrement Amount to increase speed
     */
    public void accelerate(double speedIncrement) {
        currentSpeed += speedIncrement;
        System.out.println("Accelerating. Current speed: " + currentSpeed + " km/h");
    }

    /**
     * Decelerate the car by decreasing speed
     * @param speedDecrement Amount to decrease speed
     */
    public void decelerate(double speedDecrement) {
        currentSpeed = Math.max(0, currentSpeed - speedDecrement);
        System.out.println("Decelerating. Current speed: " + currentSpeed + " km/h");
    }

    /**
     * Refuel the car by adding fuel to tank
     * @param fuelAmount Amount of fuel to add
     * @throws CarException If fuel amount is negative
     */
    public void refuel(double fuelAmount) throws CarException {
        if (fuelAmount < 0) {
            throw new CarException("Fuel amount cannot be negative");
        }
        fuelLevel += fuelAmount;
        System.out.println("Refueled. Current fuel level: " + fuelLevel + " liters");
    }

    /**
     * Calculate fuel consumption based on distance and efficiency
     * @param distance Distance travelled
     * @return Fuel consumed
     * @throws CarException If distance is negative
     */
    public double calculateFuelConsumption(double distance) throws CarException {
        if (distance < 0) {
            throw new CarException("Distance travelled cannot be negative");
        }
        distanceTravelled += distance;
        double fuelConsumed = distance / fuelEfficiency;
        System.out.println("Fuel consumed: " + fuelConsumed + " liters");
        return fuelConsumed;
    }

    /**
     * Main method to demonstrate Car class functionality
