     * 
     * @param fuelEfficiency The fuel efficiency to validate.
     * @throws CarException if the fuel efficiency is not positive.
     */
    private void validateFuelEfficiency(double fuelEfficiency) throws CarException {
        if (fuelEfficiency <= 0) {
            throw new CarException("Fuel efficiency must be a positive value");
        }
    }

    /**
     * Validates the distance travelled by the car.
     * 
     * @param distance The distance to validate.
     * @throws CarException if the distance is negative.
     */
    private void validateDistance(double distance) throws CarException {
        if (distance < 0) {
            throw new CarException("Distance travelled cannot be negative");
        }
    }

    /**
     * Increases the car's speed by a specified amount.
     * 
     * @param increment The amount by which to increase the speed (in mph).
     */
    public void accelerate(double increment) {
        this.speed += increment;
        System.out.println("Accelerating. Current speed: " + this.speed + " mph");
    }

    /**
     * Decreases the car's speed by a specified amount.
     * 
     * @param decrement The amount by which to decrease the speed (in mph).
     */
    public void decelerate(double decrement) {
        if (this.speed - decrement >= 0) {
            this.speed -= decrement;
        } else {
            this.speed = 0;
        }
        System.out.println("Decelerating. Current speed: " + this.speed + " mph");
    }

    /**
     * Simulates driving the car for a certain time, updating the distance travelled.
     * 
     * @param hours The time driven in hours.
     * @throws CarException if the calculated distance is negative.
     */
    public void drive(double hours) throws CarException {
        double distance = this.speed * hours;
        validateDistance(distance);
        this.distanceTravelled += distance;
        System.out.println("Driven for " + hours + " hours. Distance covered: " + distance + " miles");
    }

    /**
     * Calculates the fuel consumed based on distance travelled and fuel efficiency.
     * 
     * @return The fuel consumed in gallons.
     */
    public double calculateFuelConsumption() {
        return this.distanceTravelled / this.fuelEfficiency;
    }

    /**
     * Returns a string representation of the car's details.
     * 
     * @return A string containing the car's make, model, year, and fuel efficiency.
     */
    @Override
    public String toString() {
        return "Car Details: " + this.year + " " + this.make + " " + this.model + 
               ", Fuel Efficiency: " + this.fuelEfficiency + " MPG";
    }

