#include <vector>
#include <iostream>

/**
 * @brief Swaps two elements in a vector at given indices.
 * 
 * @param vec Reference to the vector containing elements to swap
 * @param index1 First element's index
 * @param index2 Second element's index
 */
void swapElements(std::vector<int>& vec, size_t index1, size_t index2) {
    int temp = vec[index1];
    vec[index1] = vec[index2];
    vec[index2] = temp;
}

/**
 * @brief Filters non-negative numbers from input and sorts them in ascending order.
 * 
 * This function first filters out all negative numbers from the input list,
 * then sorts the remaining non-negative numbers using bubble sort algorithm.
 * 
 * @param input List of integers to process
 * @return std::vector<int> Sorted list of non-negative integers
 */
std::vector<int> getPositiveAndSort(const std::vector<int>& input) {
    // Filter out negative numbers
    std::vector<int> nonNegative;
    for (int num : input) {
        if (num >= 0) {
            nonNegative.push_back(num);
        }
    }

    // Bubble sort implementation
    for (size_t i = 0; i < nonNegative.size(); ++i) {
        for (size_t j = 0; j < nonNegative.size() - 1; ++j) {
            if (nonNegative[j] > nonNegative[j + 1]) {
                swapElements(nonNegative, j, j + 1);
            }
        }
    }

    return nonNegative;
}

int main() {
    // Test cases
    std::vector<int> test1 = getPositiveAndSort({-1, 2, -4, 5, 6});
    std::vector<int> test2 = getPositiveAndSort({5, 3, -5, 2, -3, 3, 9, 0, 123, 1, -10});

    // Output results
    std::cout << "Test 1: ";
    for (int num : test1) {
        std::cout << num << " ";
