```python
import json

def process_people_data(json_string):
    """
    Processes a JSON string containing people's data to extract education and occupation details,
    calculates the average age, and restructures the information into a new JSON format.

    Args:
        json_string (str/dict): JSON string or dictionary containing people's data with nested structures.

    Returns:
        dict: A new JSON structure containing:
            - 'people_education_occupation': List of dictionaries with each person's name, education, and occupation.
            - 'average_age': The calculated average age of all people.
    """
    # Parse the JSON string if it's a string, otherwise use as-is if already a dict
    if isinstance(json_string, str):
        data = json.loads(json_string)
    else:
        data = json_string

    total_age = 0
    people_count = 0
    people_info = []

    # Iterate through each person in the 'people' array
    for person in data.get('people', []):
        name = person.get('name', '')
        age = person.get('age', 0)
        details = person.get('details', {})

        # Extract education details
        education = details.get('education', {})
        school = education.get('school', '')
        major = education.get('major', '')

        # Extract occupation details
        occupation = details.get('occupation', {})
        current_job = occupation.get('current', '')
        experience = occupation.get('experience', 0)

        # Accumulate age for average calculation
        total_age += age
        people_count += 1

        # Append extracted info to the new structure
        people_info.append({
            'name': name,
            'education': {
                'school': school,
                'major': major
            },
            'occupation': {
                'current': current_job,
                'experience': experience
            }
        })

    # Calculate average age (handle division by zero if empty list)
    average_age = total_age / people_count if people_count > 0 else 0

    # Create the new JSON structure
    result = {
        'people_education_occupation': people_info,
        'average_age': average_age
    }

    return result

