#include <iostream>
#include <string>

class Product {
private:
    std::string name;
    int quantity;

public:
    // Constructor to initialize product name and quantity
    Product(const std::string& productName, int initialQuantity)
        : name(productName), quantity(initialQuantity) {}

    // Method to add stock to the product
    void addStock(int amount) {
        if (amount > 0) {
            quantity += amount;
            std::cout << "Added " << amount << " to " << name << ". New quantity: " << quantity << std::endl;
        } else {
            std::cout << "Invalid amount to add." << std::endl;
        }
    }

    // Method to sell the product
    void sell(int amount) {
        if (amount > 0) {
            if (amount <= quantity) {
                quantity -= amount;
                std::cout << "Sold " << amount << " of " << name << ". Remaining quantity: " << quantity << std::endl;
            } else {
                std::cout << "Cannot sell " << amount << " of " << name << ". Insufficient stock." << std::endl;
            }
        } else {
            std::cout << "Invalid amount to sell." << std::endl;
        }
    }
};

int main() {
    // Creating instances of Product
    Product apple("Apple", 100);
    Product banana("Banana", 150);

    // Demonstrating adding stock
    apple.addStock(50);
    banana.addStock(20);

    // Demonstrating selling products
    apple.sell(30);
    banana.sell(200);  // This should trigger insufficient stock message
    banana.sell(100);

    return 0;
}
```

