#include <iostream>
#include <string>
#include <vector>
#include <unordered_map>

class Product {
private:
    std::string name;
    int quantity;

public:
    // Constructor with optimized initialization
    Product(const std::string& productName, int initialQuantity = 0) 
        : name(productName), quantity(initialQuantity) {}

    // Optimized method for adding stock with input validation
    void addStock(int amount) {
        if (amount > 0) {
            quantity += amount;
        } else {
            std::cerr << "Invalid stock addition amount for " << name << std::endl;
        }
    }

    // Optimized method for selling products with error handling
    bool sell(int amount) {
        if (amount <= 0) {
            std::cerr << "Invalid sale amount for " << name << std::endl;
            return false;
        }

        if (amount <= quantity) {
            quantity -= amount;
            return true;
        } else {
            std::cerr << "Insufficient stock for " << name 
                      << ". Available: " << quantity 
                      << ", Requested: " << amount << std::endl;
            return false;
        }
    }

    // Getter methods for efficient access
    std::string getName() const { return name; }
    int getQuantity() const { return quantity; }
};

class InventoryManager {
private:
    // Using unordered_map for O(1) product lookup
    std::unordered_map<std::string, Product> inventory;

public:
    // Efficient method to add a new product
    void addProduct(const Product& product) {
        inventory[product.getName()] = product;
    }

    // Efficient method to get product by name
    Product* getProduct(const std::string& name) {
        auto it = inventory.find(name);
        return (it != inventory.end()) ? &(it->second) : nullptr;
    }
};

int main() {
    // Time and space efficient inventory management demonstration
    InventoryManager manager;

    // Create products with optimized object creation
    Product apple("Apple", 100);
    Product banana("Banana", 50);

    // Add products to inventory
    manager.addProduct(apple);
    manager.addProduct(banana);

