    AdcReference reference;
    AdcPrescaler prescaler;
    uint8_t resolution;  // In bits (typically 8, 10, or 12)
} AdcConfig;

// Initialize the ADC with the given configuration settings
void adc_init(const AdcConfig* config);

// Read analog value from the specified ADC channel (O(1) time complexity)
uint16_t adc_read_channel(uint8_t channel);

// Read and average multiple samples (O(n) time complexity)
uint16_t adc_read_average(uint8_t channel, uint8_t samples);

// Convert raw ADC value to millivolts (O(1) time complexity)
uint16_t adc_to_millivolts(uint16_t raw_value, uint16_t ref_mv);

#endif // ADC_H

// adc.c
#include "adc.h"
#include <avr/io.h>

static AdcConfig current_config;

void adc_init(const AdcConfig* config) {
    if (config == NULL) return;

    current_config = *config;

    // Set reference voltage (optimized bit manipulation)
    ADMUX = (ADMUX & 0x3F) | ((config->reference & 0x03) << 6);

    // Set prescaler (optimized lookup)
    const uint8_t prescaler_bits[] = {1, 2, 3, 4, 5, 6, 7};
    ADCSRA = (ADCSRA & 0xF8) | (prescaler_bits[config->prescaler] & 0x07);

    // Enable ADC
    ADCSRA |= (1 << ADEN);
}

uint16_t adc_read_channel(uint8_t channel) {
    // Validate channel (prevent out-of-bounds access)
    if (channel > 31) return 0;

    // Set channel (single operation)
    ADMUX = (ADMUX & 0xE0) | (channel & 0x1F);

    // Start conversion (single bit operation)
    ADCSRA |= (1 << ADSC);

    // Wait for conversion (busy-wait but most efficient for small MCUs)
    while (ADCSRA & (1 << ADSC));

    // Read result (single operation)
    return ADC;
}

uint16_t adc_read_average(uint8_t channel, uint8_t samples) {
    if (samples == 0) return 0;

    uint32_t sum = 0;
    for (uint8_t i = 0; i < samples; i++) {
        sum += adc_read_channel(channel);
    }
    return (uint16_t)(sum / samples);
}

uint16_t adc_to_millivolts(uint16_t raw_value, uint16_t ref_mv) {
    // Use 32-bit math to prevent overflow and maintain precision
    return (uint16_t)(((uint32_t)raw_value * ref_mv) / (1 << current_config.resolution));
}
