    // Set left adjust result if configured
    if (config->left_adjust) {
        ADMUX |= (1 << ADLAR);
    } else {
        ADMUX &= ~(1 << ADLAR);
    }

    // Set prescaler
    ADCSRA = (ADCSRA & ~0x07) | (config->prescaler & 0x07);

    // Enable ADC
    ADCSRA |= (1 << ADEN);
}

