    // Validate channel (assuming 8 channels max)
    if (channel > 7) return 0;

    // Select channel
    ADMUX = (ADMUX & 0xF0) | (channel & 0x0F);

    // Start conversion
    ADCSRA |= (1 << ADSC);

    // Wait for conversion to complete
    while (ADCSRA & (1 << ADSC));

    // Read result based on resolution and alignment
    if (current_config.left_adjust) {
        return ADCH << (current_config.resolution - 8);
    } else {
