    // Set ADC resolution (prescaler)
    uint8_t prescaler = 0;
    if (config->clock_frequency <= 1000000) prescaler = 0x07;  // Divide by 128
    else if (config->clock_frequency <= 2000000) prescaler = 0x06;  // Divide by 64

    ADCSRA = (1 << ADEN) | prescaler;  // Enable ADC and set prescaler
