float adc_apply_calibration(uint16_t raw_value, const AdcChannelConfig* channel_config);
uint16_t adc_average_readings(uint8_t channel, uint8_t num_samples);

#endif // ADC_H

// adc.c
#include "adc.h"
#include <avr/io.h>

// Optimized ADC initialization
void adc_init(const AdcConfig* config) {
    // Time Complexity: O(1)
    // Space Complexity: Minimal stack usage

    // Configure reference voltage
    switch (config->reference_voltage) {
        case ADC_REF_EXTERNAL:
            ADMUX &= ~((1 << REFS1) | (1 << REFS0));
            break;
        case ADC_REF_INTERNAL_1V1:
            ADMUX = (1 << REFS1);
            break;
        case ADC_REF_INTERNAL_2V56:
            ADMUX = (1 << REFS1) | (1 << REFS0);
            break;
    }

    // Set resolution (for 10-bit, left adjust = 0)
    if (config->resolution == ADC_8BIT_RESOLUTION) {
        ADMUX |= (1 << ADLAR);  // Left adjust for 8-bit resolution
    }

    // Configure ADC prescaler for optimal sampling rate
    ADCSRA = (1 << ADEN) | (config->prescaler & 0x07);
}

// Efficient channel reading with minimal overhead
uint16_t adc_read_channel(uint8_t channel) {
    // Time Complexity: O(1)
    // Space Complexity: Minimal stack usage

    // Select ADC channel with safety mask
    ADMUX = (ADMUX & 0xF0) | (channel & 0x0F);

    // Start conversion
    ADCSRA |= (1 << ADSC);

    // Wait for conversion to complete (busy-wait with minimal cycles)
    while (ADCSRA & (1 << ADSC));

    // Return 10-bit result
    return ADC;
}

// Advanced voltage calculation with calibration
float adc_get_voltage(uint16_t raw_value, const AdcConfig* config) {
    float max_voltage = (config->reference_voltage == ADC_REF_INTERNAL_2V56) ? 2.56 : 
                        (config->reference_voltage == ADC_REF_INTERNAL_1V1) ? 1.1 : 5.0;

    return (raw_value * max_voltage) / ((1 << config->resolution) - 1);
}

// Averaging multiple readings for noise reduction
uint16_t adc_average_readings(uint8_t channel, uint8_t num_samples) {
    // Time Complexity: O(n), where n is num_samples
    uint32_t sum = 0;

    for (uint8_t i = 0; i < num_samples; i++) {
        sum += adc_read_channel(channel);
    }

    return sum / num_samples;
}
```

