        Set<String> authorized = new HashSet<String>();
        double avg = getAverage(executionCounts);

        // Calculate the threshold value based on the average
        double thresholdValue = threshold * avg;

        // Iterate through the unique resources in the multiset
        for (String res : executionCounts.elementSet()) {
            // Check if the execution count meets the threshold condition
            if (executionCounts.count(res) >= thresholdValue) {
                authorized.add(res); // Add the resource to the authorized set
            }
        }

        return authorized; // Return the set of authorized resources
    }

    // Assuming getAverage is a provided method
    private double getAverage(Multiset<String> executionCounts) {
        // Implementation for average calculation
        double total = 0;
        int count = 0;
        for (String res : executionCounts.elementSet()) {
            total += executionCounts.count(res);
            count += 1; // Increment the count for each unique resource
        }
        return count > 0 ? total / count : 0; // Return average or 0 if count is 0
    }
}
