```java
import java.io.IOException;
import java.nio.file.*;
import java.util.HashMap;
import java.util.Map;

public class IOUtils {

    public static Map<String, Path> getAllFiles(String directoryPath) {
        Map<String, Path> fileMap = new HashMap<>();
        Path dirPath = Paths.get(directoryPath);

        try {
            Files.walk(dirPath)
                .filter(Files::isRegularFile)
                .forEach(file -> {
                    String relativePath = dirPath.relativize(file).toString();
                    fileMap.put(relativePath, file.toAbsolutePath());
                });
        } catch (IOException e) {
            e.printStackTrace(); // Consider logging or handling the exception as needed.
        }

        return fileMap;
    }
}
```

