import numpy as np

def concatenate_nested(*args):
    if not args:
        return None

    # Flatten the nested structure to get all elements
    def flatten(nested):
        flattened = []
        for item in nested:
            if isinstance(item, (list, tuple)):
                flattened.extend(flatten(item))
            else:
                flattened.append(item)
        return flattened

    # Get the first element of the first argument's first element
    first_element = args[0][0]
    if isinstance(first_element, dict):
        # Case 1: Concatenate dictionaries
        result = {}
        for arg in args:
            items = flatten(arg)
            for item in items:
                if isinstance(item, dict):
                    result.update(item)
        return result
    elif isinstance(first_element, np.ndarray):
        # Case 2: Concatenate numpy arrays
        arrays = []
        for arg in args:
            items = flatten(arg)
            for item in items:
                if isinstance(item, np.ndarray):
                    arrays.append(item)
        return np.concatenate(arrays)
    elif isinstance(first_element, list) and len(first_element) > 0 and isinstance(first_element[0], np.ndarray):
        # Case 3: First element is list containing numpy arrays
        arrays = []
        for arg in args:
            items = flatten(arg)
            for item in items:
                if isinstance(item, list):
                    for subitem in item:
                        if isinstance(subitem, np.ndarray):
                            arrays.append(subitem)
        return np.concatenate(arrays)
    else:
        raise ValueError("Unsupported input structure for concatenation")

