    if not args or not args[0]:
        return None  # Return None if no input is provided

    first_element = args[0][0]

    # Check the type of the first element to determine the concatenation method
    if isinstance(first_element, dict):
        # Initialize an empty dictionary for merging
        result = {}
        for nested_list in args:
            for item in nested_list:
                if isinstance(item, dict):
                    result.update(item)  # Update the result dictionary with items
        return result

    elif isinstance(first_element, np.ndarray):
        # Initialize a list to hold numpy arrays
        result = []
        for nested_list in args:
            for item in nested_list:
                if isinstance(item, np.ndarray):
                    result.append(item)  # Append numpy arrays to the result list
        return np.concatenate(result) if result else np.array([])  # Return concatenated array

    elif isinstance(first_element, list) and isinstance(first_element[0], np.ndarray):
        # Initialize a list to hold numpy arrays
        result = []
        for nested_list in args:
            for item in nested_list:
