```cpp
#include <SFML/Network.hpp>
#include <iostream>
#include <thread>
#include <atomic>

// Shared constants
const unsigned short PORT = 55001;
const std::string SERVER_IP = "127.0.0.1";
const std::string WELCOME_MESSAGE = "Welcome to the server!";

// Server implementation
void runServer() {
    sf::TcpListener listener;

    // Bind the listener to a specific port
    if (listener.listen(PORT) != sf::Socket::Done) {
        std::cerr << "Failed to listen on port " << PORT << std::endl;
        return;
    }

    std::cout << "Server listening on port " << PORT << std::endl;

    // Use atomic flag for thread-safe running state
    std::atomic<bool> running(true);

    while (running) {
        // Create a socket for the client
        sf::TcpSocket client;

        // Wait for and accept a new connection
        if (listener.accept(client) == sf::Socket::Done) {
            std::cout << "New client connected" << std::endl;

            // Send welcome message
            sf::Socket::Status status = client.send(
                WELCOME_MESSAGE.c_str(), 
                WELCOME_MESSAGE.length() + 1
            );

            if (status != sf::Socket::Done) {
                std::cerr << "Failed to send message to client" << std::endl;
            }

            // Close the client connection
            client.disconnect();
        }
    }
}

// Client implementation
void runClient() {
    sf::TcpSocket socket;

    // Attempt to connect to the server
    if (socket.connect(SERVER_IP, PORT) == sf::Socket::Done) {
        std::cout << "Connected to server" << std::endl;

        // Buffer to receive message
        char buffer[256] = {0};  // Increased buffer size for safety
        std::size_t received = 0;

        // Receive message from server
        sf::Socket::Status status = socket.receive(buffer, sizeof(buffer), received);

        if (status == sf::Socket::Done) {
            std::cout << "Received: " << buffer << std::endl;
        } else {
            std::cerr << "Failed to receive message" << std::endl;
        }

        // Disconnect from the server
        socket.disconnect();
    } else {
        std::cerr << "Failed to connect to server" << std::endl;
    }
}

int main() {
    // Run server and client in separate threads
    std::thread serverThread(runServer);

    // Small delay to ensure server is up
    std::this_thread::sleep_for(std::chrono::milliseconds(100));

    // Run client
    runClient();

    // Wait for server thread to complete
    serverThread.join();

    return 0;
}
