                std::cerr << "Failed to send message" << std::endl;
            }

            // Cleanup client socket
            delete client;
        }
    }
}

// Optimization: Client connection in separate function
void handleClient() {
    sf::TcpSocket socket;

    // Connection with timeout handling
    if (socket.connect("127.0.0.1", 55001, sf::seconds(5)) == sf::Socket::Done) {
        // Optimization: Use vector for dynamic buffer
        std::vector<char> buffer(100);
        std::size_t received;

        // Receive with error checking
        if (socket.receive(buffer.data(), buffer.size(), received) == sf::Socket::Done) {
            std::cout << "Received: " << buffer.data() << std::endl;
        } else {
            std::cerr << "Failed to receive message" << std::endl;
        }
    } else {
        std::cerr << "Failed to connect to server" << std::endl;
    }
}

int main() {
    // Optimization: Use threads for concurrent server and client
    std::thread serverThread(handleServer);

    // Small delay to ensure server is ready
    std::this_thread::sleep_for(std::chrono::milliseconds(100));

    std::thread clientThread(handleClient);

    // Wait for threads to complete
    serverThread.join();
    clientThread.join();

    return 0;
}
```

