from flask import Flask, jsonify, request, make_response
import csv
import io

app = Flask(__name__)

# Mock user session for demonstration purposes
# In a real application, this would be managed by a session or token
current_user = {
    'logged_in': True,  # Change to False to simulate not logged in
    'permissions': ['access_keyword']  # Adjust permissions for testing
}

def login_required(func):
    def wrapper(*args, **kwargs):
        if not current_user.get('logged_in'):
            return jsonify({"error": "User must be logged in to access this resource."}), 403
        return func(*args, **kwargs)
    return wrapper

def keyword_access_check(func):
    def wrapper(keyword, *args, **kwargs):
        if 'access_keyword' not in current_user.get('permissions', []):
            return jsonify({"error": "User does not have permission to access this keyword."}), 403
        return func(keyword, *args, **kwargs)
    return wrapper

@login_required
@keyword_access_check
def keyword_csv(keyword):
    # Simulate data retrieval for the given keyword
    responses = [
        {"keyword": keyword, "response": "Sample response 1"},
        {"keyword": keyword, "response": "Sample response 2"},
    ]

    # Create a CSV response
    output = io.StringIO()
    writer = csv.DictWriter(output, fieldnames=["keyword", "response"])
    writer.writeheader()
    writer.writerows(responses)

    response = make_response(output.getvalue())
    response.headers['Content-Disposition'] = f'attachment; filename={keyword}_responses.csv'
    response.headers['Content-Type'] = 'text/csv'

    return response

@app.route('/keyword_csv/<keyword>', methods=['GET'])
def get_keyword_csv(keyword):
    return keyword_csv(keyword)

