```cpp
#include <iostream>

class Node {
public:
    int data;
    Node* next;
    Node* prev;

    Node(int value) : data(value), next(nullptr), prev(nullptr) {}
};

class DoublyLinkedList {
private:
    Node* head;

public:
    DoublyLinkedList() {
        head = new Node(0); // Initialize with an empty node
    }

    ~DoublyLinkedList() {
        clear();
        delete head; // Clean up the head node
    }

    void addNode(int value) {
        Node* newNode = new Node(value);
        Node* temp = head;

        // Traverse to the end of the list
        while (temp->next != nullptr) {
            temp = temp->next;
        }

        temp->next = newNode;
        newNode->prev = temp;
    }

    void deleteNode(int value) {
        Node* temp = head->next; // Start from the first real node

        // Search for the node to delete
        while (temp != nullptr && temp->data != value) {
            temp = temp->next;
        }

        if (temp != nullptr) { // Node found
            if (temp->prev != nullptr) {
                temp->prev->next = temp->next;
            }
            if (temp->next != nullptr) {
                temp->next->prev = temp->prev;
            }
            delete temp; // Free the memory of the deleted node
        } else {
            std::cout << "Node with value " << value << " not found." << std::endl;
        }
    }

    void display() {
        Node* temp = head->next; // Skip the empty head node
        while (temp != nullptr) {
            std::cout << temp->data << " ";
            temp = temp->next;
        }
        std::cout << std::endl;
    }

    void clear() {
        Node* temp = head->next;
        while (temp != nullptr) {
            Node* nextNode = temp->next;
            delete temp;
            temp = nextNode;
        }
        head->next = nullptr; // Reset the list to empty state
    }
};

int main() {
    DoublyLinkedList dll;

    // Adding nodes to the list
    dll.addNode(10);
    dll.addNode(20);
    dll.addNode(30);
    std::cout << "List after adding nodes: ";
    dll.display();

    // Deleting a node
    dll.deleteNode(20);
    std::cout << "List after deleting node with value 20: ";
    dll.display();

    // Trying to delete a non-existent node
    dll.deleteNode(40);

    // Clear the list
    dll.clear();
    std::cout << "List after clearing: ";
    dll.display();

    return 0;
}
```

