```cpp
#include <iostream>
#include <stdexcept>

// Optimized Doubly Linked List Class
class DoublyLinkedList {
private:
    // Node structure with optimized memory layout
    struct Node {
        int data;
        Node* prev;
        Node* next;

        // Efficient constructor
        Node(int val = 0) : data(val), prev(nullptr), next(nullptr) {}
    };

    Node* head;  // Always an empty start node
    int size;    // Track list size for O(1) length retrieval

public:
    // Constructor - Initialize empty list
    DoublyLinkedList() : head(new Node()), size(0) {}

    // Destructor - Prevent memory leaks
    ~DoublyLinkedList() {
        while (head != nullptr) {
            Node* temp = head;
            head = head->next;
            delete temp;
        }
    }

    // Time Complexity: O(1)
    void insertFront(int value) {
        Node* newNode = new Node(value);

        if (head->next == nullptr) {
            // First insertion
            head->next = newNode;
            newNode->prev = head;
        } else {
            // Insert before first element
            newNode->next = head->next;
            newNode->prev = head;
            head->next->prev = newNode;
            head->next = newNode;
        }
        size++;
    }

    // Time Complexity: O(1)
    void insertBack(int value) {
        Node* newNode = new Node(value);

        // Find last node
        Node* current = head;
        while (current->next != nullptr) {
            current = current->next;
        }

        // Link new node
        current->next = newNode;
        newNode->prev = current;
        size++;
    }

    // Time Complexity: O(1)
    void deleteFront() {
        if (size == 0) {
            throw std::runtime_error("List is empty");
        }

        Node* toDelete = head->next;
        head->next = toDelete->next;

        if (toDelete->next) {
            toDelete->next->prev = head;
        }

        delete toDelete;
        size--;
    }

    // Time Complexity: O(1)
    void deleteBack() {
        if (size == 0) {
            throw std::runtime_error("List is empty");
        }

        // Find last node
        Node* current = head;
        while (current->next != nullptr) {
            current = current->next;
        }

        // Unlink and delete
        current->prev->next = nullptr;
        delete current;
        size--;
    }

    // Time Complexity: O(n)
    void display() const {
