Here's an optimized C++ implementation of a doubly linked list that addresses time and space complexity considerations:

```cpp
#include <iostream>
#include <stdexcept>

// Optimized Doubly Linked List Class
class DoublyLinkedList {
private:
    // Node structure with optimized memory layout
    struct Node {
        int data;
        Node* prev;
        Node* next;

        // Efficient constructor
        Node(int val) : data(val), prev(nullptr), next(nullptr) {}
    };

    Node* head;  // Sentinel node (always empty)
    int size;    // Tracking list size for O(1) length retrieval

public:
    // Constructor - O(1) time complexity
    DoublyLinkedList() : head(new Node(0)), size(0) {}

    // Destructor to prevent memory leaks - O(n) time complexity
    ~DoublyLinkedList() {
        while (head->next) {
            Node* temp = head->next;
            head->next = temp->next;
            delete temp;
        }
        delete head;
    }

    // Add node at the end - O(1) time complexity
    void append(int value) {
        Node* newNode = new Node(value);

        // If list is empty
        if (!head->next) {
            head->next = newNode;
            newNode->prev = head;
        }
        else {
            // Find last node
            Node* current = head;
            while (current->next) {
                current = current->next;
            }

            current->next = newNode;
            newNode->prev = current;
        }

        size++;
    }

    // Delete node by value - O(n) time complexity
    void remove(int value) {
        Node* current = head->next;

        while (current) {
            if (current->data == value) {
                // Adjust links
                if (current->prev) 
                    current->prev->next = current->next;

                if (current->next) 
                    current->next->prev = current->prev;

                Node* toDelete = current;
                current = current->next;

                delete toDelete;
                size--;

                return;
            }
            current = current->next;
        }
    }

    // Insert node at specific position - O(n) time complexity
    void insertAt(int value, int position) {
        if (position < 0 || position > size) {
            throw std::out_of_range("Invalid position");
        }

        Node* newNode = new Node(value);
        Node* current = head;

        // Traverse to insertion point
        for (int i = 0; i < position; ++i) {
            current = current->next;
        }

        // Link new node
        newNode->next = current->next;
        newNode->prev = current;

        if (current->next) 
            current->next->prev = newNode;

        current->next = newNode;
        size++;
    }

    // Print list - O(n) time complexity
    void display() {
        Node* current = head->next;
        while (current) {
            std::cout << current->data << " ";
            current = current->next;
        }
        std::cout << std::endl;
    }

    // Get list size - O(1) time complexity
    int getSize() const {
        return size;
    }
};

// Demonstration
int main() {
    DoublyLinkedList list;

    // Append elements
    list.append(10);
    list.append(20);
    list.append(30);

    std::cout << "Initial List: ";
    list.display();

    // Insert at specific position
    list.insertAt(15, 1);

    std::cout << "After Insertion: ";
    list.display();

    // Remove element
    list.remove(20);

    std::cout << "After Removal: ";
    list.display();

    std::cout << "List Size: " << list.getSize() << std::endl;

    return 0;
}
```

