    // Optimized data storage with minimal memory footprint
    std::string fullName;
    std::string academicId;
    std::vector<double> courseGrades;

    // Cached GPA calculation to avoid redundant computations
    mutable double cachedGPA = -1.0;

public:
    // Efficient constructor with move semantics
    StudentPortfolio(std::string name, std::string id) 
        : fullName(std::move(name)), 
          academicId(std::move(id)) {}

