        }

        // Prevent division by zero
        if (courseGrades.empty()) {
            return 0.0;
        }

        // Parallel-friendly reduction for GPA calculation
        double totalPoints = std::accumulate(
            courseGrades.begin(), 
            courseGrades.end(), 
