        (500, 'D'),
        (400, 'CD'),
        (100, 'C'),
        (90, 'XC'),
        (50, 'L'),
        (40, 'XL'),
        (10, 'X'),
        (9, 'IX'),
        (5, 'V'),
        (4, 'IV'),
        (1, 'I')
    ]

    SUBTRACTIVE_NUMERALS = {k: v for k, v in NUMERALS if len(v) == 2}
    STANDARD_NUMERALS = [(k, v) for k, v in NUMERALS if len(v) == 1]

    # Validation functions
    def is_valid_roman(s):
        if not isinstance(s, str):
            return False
        if not s:
            return False
        # Check for invalid characters
        valid_chars = {'I', 'V', 'X', 'L', 'C', 'D', 'M', '', 'v', 'x', 'l', 'c', 'd', 'm'}
        for char in s:
            if char.lower() not in valid_chars:
                return False
        return True

    def is_valid_int(n):
        return isinstance(n, int) and 1 <= n <= 1000000

    # Conversion functions
    def int_to_roman(n):
        if validate and not is_valid_int(n):
            raise ValueError("Invalid integer input. Must be between 1 and 1,000,000")

        result = []
