            if include_subtractive:
                while n >= value:
                    result.append(numeral)
                    n -= value
            else:
                # Find the standard numeral (non-subtractive)
                standard_value, standard_numeral = next(((v, n) for v, n in STANDARD_NUMERALS if v <= value), (None, None))
                if standard_value:
                    count = n // standard_value
                    result.append(standard_numeral * count)
                    n -= standard_value * count

        roman = ''.join(result)

        if format_output:
            # Group by 5000s for formatting
            parts = []
            i = 0
            while i < len(roman):
                if roman[i] in {'m', 'd', 'c', 'l', 'x', 'v'}:
                    # Handle overlined characters
                    if i + 1 < len(roman) and roman[i+1] == '':
                        parts.append(f"({roman[i]}{roman[i+1]})")
                        i += 2
                    else:
                        parts.append(f"({roman[i]})")
                        i += 1
                else:
                    parts.append(roman[i])
                    i += 1
            roman = ''.join(parts)

        return roman.lower()

    def roman_to_int(s):
        if validate:
