class QuickSort {
private:
    // Randomized pivot selection
    static int selectRandomPivot(int low, int high) {
        // Use current time as seed for random generator
        static std::mt19937 rng(std::chrono::steady_clock::now().time_since_epoch().count());
        std::uniform_int_distribution<int> dist(low, high);
        return dist(rng);
    }

    // Partition function for recursive approach
    static int partitionRecursive(std::vector<int>& arr, int low, int high) {
        // Select random pivot to improve average-case performance
        int pivotIndex = selectRandomPivot(low, high);
        std::swap(arr[pivotIndex], arr[high]);

        int pivot = arr[high];
        int i = low - 1;

        for (int j = low; j < high; ++j) {
            if (arr[j] <= pivot) {
                ++i;
                std::swap(arr[i], arr[j]);
            }
        }
        std::swap(arr[i + 1], arr[high]);
        return i + 1;
    }

    // Recursive QuickSort implementation
    static void quickSortRecursive(std::vector<int>& arr, int low, int high) {
        if (low < high) {
            int pivotIndex = partitionRecursive(arr, low, high);

            // Recursive calls on left and right subarrays
            quickSortRecursive(arr, low, pivotIndex - 1);
            quickSortRecursive(arr, pivotIndex + 1, high);
        }
    }

    // Partition function for iterative approach
    static int partitionIterative(std::vector<int>& arr, int low, int high) {
        int pivotIndex = selectRandomPivot(low, high);
        std::swap(arr[pivotIndex], arr[high]);

        int pivot = arr[high];
        int i = low - 1;

        for (int j = low; j < high; ++j) {
            if (arr[j] <= pivot) {
                ++i;
                std::swap(arr[i], arr[j]);
            }
        }
        std::swap(arr[i + 1], arr[high]);
        return i + 1;
    }

    // Iterative QuickSort implementation
    static void quickSortIterative(std::vector<int>& arr, int low, int high) {
        std::stack<std::pair<int, int>> stack;
        stack.push({low, high});

        while (!stack.empty()) {
            low = stack.top().first;
            high = stack.top().second;
            stack.pop();

            if (low < high) {
                int pivotIndex = partitionIterative(arr, low, high);

                // Push subarrays to stack
                if (pivotIndex - 1 > low) {
                    stack.push({low, pivotIndex - 1});
                }
                if (pivotIndex + 1 < high) {
                    stack.push({pivotIndex + 1, high});
                }
            }
        }
    }

public:
    // Public method for recursive QuickSort
    static void recursiveSort(std::vector<int>& arr) {
        if (arr.empty()) return;
        quickSortRecursive(arr, 0, arr.size() - 1);
    }

    // Public method for iterative QuickSort
    static void iterativeSort(std::vector<int>& arr) {
        if (arr.empty()) return;
        quickSortIterative(arr, 0, arr.size() - 1);
    }
