class QuickSort {
private:
    // Randomized pivot selection
    static int randomizedPartition(std::vector<int>& arr, int low, int high) {
        // Use current time as seed for random generator
        unsigned seed = std::chrono::system_clock::now().time_since_epoch().count();
        std::default_random_engine generator(seed);

        // Generate random index between low and high
        std::uniform_int_distribution<int> distribution(low, high);
        int randomPivotIndex = distribution(generator);

        // Swap random pivot with last element
        std::swap(arr[randomPivotIndex], arr[high]);

        return partition(arr, low, high);
    }

    // Partition function for Quicksort
    static int partition(std::vector<int>& arr, int low, int high) {
        int pivot = arr[high];
        int i = low - 1;

        for (int j = low; j < high; ++j) {
            if (arr[j] <= pivot) {
                ++i;
                std::swap(arr[i], arr[j]);
            }
        }

        std::swap(arr[i + 1], arr[high]);
        return i + 1;
    }

    // Recursive Quicksort implementation
    static void quickSortRecursive(std::vector<int>& arr, int low, int high) {
        if (low < high) {
            // Use randomized pivot selection
            int pivotIndex = randomizedPartition(arr, low, high);

            // Recursively sort left and right subarrays
            quickSortRecursive(arr, low, pivotIndex - 1);
            quickSortRecursive(arr, pivotIndex + 1, high);
        }
    }

    // Iterative Quicksort implementation using stack
    static void quickSortIterative(std::vector<int>& arr, int low, int high) {
        // Create an auxiliary stack
        std::vector<int> stack(high - low + 1);

        // Initialize top of stack
        int top = -1;

        // Push initial low and high values to stack
        stack[++top] = low;
        stack[++top] = high;

        // Keep popping from stack while it's not empty
        while (top >= 0) {
            // Pop high and low
            high = stack[top--];
            low = stack[top--];

            // Partition the array
            int pivotIndex = randomizedPartition(arr, low, high);

            // If there are elements on left side of pivot, push left subarray
            if (pivotIndex - 1 > low) {
                stack[++top] = low;
                stack[++top] = pivotIndex - 1;
            }

            // If there are elements on right side of pivot, push right subarray
            if (pivotIndex + 1 < high) {
                stack[++top] = pivotIndex + 1;
                stack[++top] = high;
            }
        }
    }

public:
    // Public interface for recursive Quicksort
    static void recursiveSort(std::vector<int>& arr) {
        if (arr.empty()) return;
        quickSortRecursive(arr, 0, arr.size() - 1);
    }

    // Public interface for iterative Quicksort
    static void iterativeSort(std::vector<int>& arr) {
        if (arr.empty()) return;
