string match_parens(vector<string> lst) {
    string s1 = lst[0];
    string s2 = lst[1];

    // Function to check if a string has balanced parentheses
    auto is_balanced = [](const string& s) -> bool {
        int balance = 0;
        for (char ch : s) {
            if (ch == '(') {
                balance++;
            } else if (ch == ')') {
                balance--;
            }
            // If balance goes negative, we have more ')' than '('
            if (balance < 0) {
                return false;
            }
        }
        // Finally, balance should be zero for a balanced string
        return balance == 0;
    };

    // Check both combinations: s1 + s2 and s2 + s1
    return (is_balanced(s1 + s2) || is_balanced(s2 + s1)) ? "Yes" : "No";
}

