class Animal:
    def __init__(self, species, habitat, diet, lifespan):
        self._species = species
        self._habitat = habitat
        self._diet = diet
        self._lifespan = lifespan

    @property
    def species(self):
        return self._species

    @property
    def habitat(self):
        return self._habitat

    @property
    def diet(self):
        return self._diet

    @property
    def lifespan(self):
        return self._lifespan

    def make_sound(self):
        raise NotImplementedError("Subclasses must implement this method")

    def __str__(self):
        return f"{self.species} (Habitat: {self.habitat}, Diet: {self.diet}, Lifespan: {self.lifespan})"


class Lion(Animal):
    def __init__(self, pride):
        super().__init__('Lion', 'Savanna', 'Carnivore', 12)
        self._pride = pride

    @property
    def pride(self):
        return self._pride

    def make_sound(self):
        return "Roar"

    def hunt(self):
        return f"The lion hunts with the {self.pride}."


class Elephant(Animal):
    def __init__(self, herd):
        super().__init__('Elephant', 'Grassland', 'Herbivore', 60)
        self._herd = herd

    @property
    def herd(self):
        return self._herd

    def make_sound(self):
        return "Trumpet"

    def trumpet(self):
        return f"The elephant trumpets with the {self.herd}."


class Eagle(Animal):
    def __init__(self, nest):
        super().__init__('Eagle', 'Mountains', 'Carnivore', 20)
        self._nest = nest

    @property
    def nest(self):
        return self._nest

    def make_sound(self):
        return "Screech"

    def soar(self):
        return "The eagle soars high in the sky."


class Habitat:
    def __init__(self):
        self.animals = []

    def add_animal(self, animal):
        if not isinstance(animal, Animal):
            raise TypeError("Only animals can be added to the habitat.")
        self.animals.append(animal)

    def remove_animal(self, animal):
        try:
            self.animals.remove(animal)
        except ValueError:
            print("Animal not found in the habitat.")

    def count_animals(self):
        return len(self.animals)

    def average_lifespan(self):
        if not self.animals:
            return 0
        total_lifespan = sum(animal.lifespan for animal in self.animals)
        return total_lifespan / len(self.animals)


# Example usage
try:
    habitat = Habitat()

    lion = Lion("Pride Rock")
    elephant = Elephant("Savannah Herd")
    eagle = Eagle("Mountain Nest")

    habitat.add_animal(lion)
    habitat.add_animal(elephant)
    habitat.add_animal(eagle)

