class Animal:
    def __init__(self, species, habitat, diet, lifespan):
        self._species = species  # Protected attribute
        self._habitat = habitat  # Protected attribute
        self._diet = diet        # Protected attribute
        self._lifespan = lifespan  # Protected attribute

    # Getter methods
    @property
    def species(self):
        return self._species

    @property
    def habitat(self):
        return self._habitat

    @property
    def diet(self):
        return self._diet

    @property
    def lifespan(self):
        return self._lifespan

    # Common behavior method to be overridden by subclasses
    def make_sound(self):
        raise NotImplementedError("Subclasses must implement this method")

    def describe(self):
        return f"A {self._species} lives in {self._habitat}, eats {self._diet}, and lives about {self._lifespan} years."


class Lion(Animal):
    def __init__(self, habitat, diet, lifespan, pride_size):
        super().__init__("Lion", habitat, diet, lifespan)
        self._pride_size = pride_size  # Protected attribute

    @property
    def pride_size(self):
        return self._pride_size

    def make_sound(self):
        return "Roar!"

    def hunt(self):
        return "The lion is hunting with its pride."

    def describe(self):
        return super().describe() + f" It lives in a pride of {self._pride_size} members."


class Elephant(Animal):
    def __init__(self, habitat, diet, lifespan, herd_size):
        super().__init__("Elephant", habitat, diet, lifespan)
        self._herd_size = herd_size  # Protected attribute

    @property
    def herd_size(self):
        return self._herd_size

    def make_sound(self):
        return "Trumpet!"

    def trumpet(self):
        return "The elephant is trumpeting loudly."

    def describe(self):
        return super().describe() + f" It travels in a herd of {self._herd_size} elephants."


class Eagle(Animal):
    def __init__(self, habitat, diet, lifespan, nest_location):
        super().__init__("Eagle", habitat, diet, lifespan)
        self._nest_location = nest_location  # Protected attribute

    @property
    def nest_location(self):
        return self._nest_location

    def make_sound(self):
        return "Screech!"

    def soar(self):
        return "The eagle is soaring high in the sky."

    def describe(self):
        return super().describe() + f" It has a nest located at {self._nest_location}."


class Habitat:
    def __init__(self, name):
        self.name = name
        self.animals = []

    def add_animal(self, animal):
        if not isinstance(animal, Animal):
            raise ValueError("Only Animal objects can be added to the habitat")
        self.animals.append(animal)

    def remove_animal(self, animal):
        if animal not in self.animals:
            raise ValueError("Animal not found in this habitat")
        self.animals.remove(animal)

    def count_animals(self):
        return len(self.animals)

    def average_lifespan(self):
        if not self.animals:
            raise ValueError("No animals in habitat to calculate average lifespan")
        total = sum(animal.lifespan for animal in self.animals)
        return total / len(self.animals)

    def list_animals(self):
        return [animal.species for animal in self.animals]

    def make_all_sounds(self):
        return [animal.make_sound() for animal in self.animals]


