        this.fuelEfficiency = fuelEfficiency;
        this.distanceTravelled = 0;
        this.currentSpeed = 0;
        this.fuelLevel = 0;
    }

    // Optimized accelerate method with speed limit
    public void accelerate(double speedIncrease) {
        // Prevent unrealistic speed increases
        final double MAX_SPEED = 250; // km/h
        currentSpeed = Math.min(currentSpeed + speedIncrease, MAX_SPEED);
    }

    // Optimized decelerate method
    public void decelerate(double speedDecrease) {
        // Prevent negative speed
        currentSpeed = Math.max(0, currentSpeed - speedDecrease);
    }

