        boolean needsComma = false;

        if (!name.isEmpty()) {
            sql.append("name = ?");
            needsComma = true;
        }
        if (!position.isEmpty()) {
            if (needsComma) sql.append(", ");
            sql.append("position = ?");
            needsComma = true;
