                "major": education["major"]
            },
            "occupation": {
                "title": occupation["current"],
                "experience_years": occupation["experience"]
            }
        }

        result["people"].append(person_data)

    # Calculate average age if there are people
    if people_count > 0:
        result["average_age"] = total_age / people_count

    return json.dumps(result, indent=2)

