from typing import List, Tuple, Dict

class Graph:
    def __init__(self, nodes: List[int], edges: List[Tuple[int, int]]):
        self.nodes = set(nodes)
        self.adjacency_list: Dict[int, set] = {node: set() for node in nodes}
        for edge in edges:
            self.add_edge(*edge)

    def add_edge(self, node1: int, node2: int):
        if node1 in self.nodes and node2 in self.nodes:
            self.adjacency_list[node1].add(node2)
            self.adjacency_list[node2].add(node1)

    def flip_pairs(self, pairs: List[Tuple[int, int]]) -> List[Tuple[int, int]]:
        flipped_pairs = []
        for node1, node2 in pairs:
            if node1 in self.adjacency_list and node2 in self.adjacency_list[node1]:
                # Flip the connection
                self.adjacency_list[node1].remove(node2)
                self.adjacency_list[node2].remove(node1)
                flipped_pairs.append((node2, node1))
                # Add the flipped connection
                self.adjacency_list[node2].add(node1)
                self.adjacency_list[node1].add(node2)
        return flipped_pairs

# Example usage
graph = Graph([1, 2, 3, 4], [(1, 2), (2, 3), (3, 4)])
flipped_pairs = graph.flip_pairs([(1, 2), (3, 4)])
print(flipped_pairs)  # Output: [(2, 1), (4, 3)]
```

