```python
import os
import re

class AssetBundler:
    def __init__(self):
        # Predefined filter methods for different asset types
        self.filters = {
            'js': {
                'uglify': self._uglify_js,
                'minify': self._minify_js
            },
            'css': {
                'autoprefixer': self._autoprefixer_css,
                'cssmin': self._cssmin_css
            }
        }

    def _read_file(self, filepath):
        """Read file contents safely."""
        try:
            with open(filepath, 'r') as file:
                return file.read()
        except IOError as e:
            print(f"Error reading file {filepath}: {e}")
            return ""

    def _write_file(self, filepath, content):
        """Write bundled content to file."""
        os.makedirs(os.path.dirname(filepath), exist_ok=True)
        with open(filepath, 'w') as file:
            file.write(content)

    def _uglify_js(self, content):
        """Simple JS uglification (remove comments and whitespace)."""
        # Remove comments
        content = re.sub(r'//.*?\n|/\*.*?\*/', '', content, flags=re.S)
        # Remove extra whitespace
        content = re.sub(r'\s+', ' ', content)
        return content

    def _minify_js(self, content):
        """Basic JS minification."""
        return self._uglify_js(content).replace('\n', '')

    def _autoprefixer_css(self, content):
        """Simulate CSS autoprefixing by adding vendor prefixes."""
        prefixes = ['-webkit-', '-moz-', '-ms-']
        for prefix in prefixes:
            content = content.replace('transform:', f'{prefix}transform:')
        return content

    def _cssmin_css(self, content):
        """Basic CSS minification."""
        # Remove comments
        content = re.sub(r'/\*.*?\*/', '', content, flags=re.S)
        # Remove whitespace
        content = re.sub(r'\s+', ' ', content)
        return content.replace('; ', ';').replace(': ', ':')

    def bundle_assets(self, input_files, filters, output_files):
        """Main bundling method."""
        for asset_type, files in input_files.items():
            # Concatenate file contents
            bundled_content = ''
            for file_path in files:
                file_content = self._read_file(file_path)

                # Apply filters
                for filter_name in filters.get(asset_type, []):
                    if filter_name in self.filters[asset_type]:
                        file_content = self.filters[asset_type][filter_name](file_content)

                bundled_content += file_content + '\n'

            # Write bundled content to output file
            output_file = output_files.get(asset_type)
            if output_file:
                self._write_file(output_file, bundled_content)
                print(f"Bundled {asset_type.upper()} files to {output_file}")

# Example usage
def main():
    bundler = AssetBundler()

    input_files = {
        "js": ["app/lib1.js", "app/main.js"],
        "css": ["styles/reset.css", "styles/main.css"]
    }

    filters = {
        "js": ["uglify"],
        "css": ["autoprefixer", "cssmin"]
    }

    output_files = {
        "js": "public/js/bundle.js",
        "css": "public/css/bundle.css"
    }

    bundler.bundle_assets(input_files, filters, output_files)

if __name__ == "__main__":
    main()
```

