import numpy as np

def concatenate_nested(*args):
    if not args:
        return None

    # Flatten the nested structure
    flattened = []
    for arg in args:
        if isinstance(arg, (list, np.ndarray, dict)):
            if isinstance(arg, dict):
                flattened.append(arg)
            else:
                # Recursively flatten lists and arrays
                stack = list(arg)
                while stack:
                    item = stack.pop()
                    if isinstance(item, (list, np.ndarray)):
                        stack.extend(item)
                    elif isinstance(item, dict):
                        flattened.append(item)
                    else:
                        pass  # Ignore other types
        else:
            pass  # Ignore non-list/array/dict arguments

    if not flattened:
        return None

    # Determine the type of the first element
    first_element = flattened[0]

    if isinstance(first_element, dict):
        # Concatenate dictionaries
        result = {}
        for d in flattened:
            if isinstance(d, dict):
                result.update(d)
        return result

    elif isinstance(first_element, np.ndarray):
        # Concatenate numpy arrays
        arrays_to_concat = [arr for arr in flattened if isinstance(arr, np.ndarray)]
        if arrays_to_concat:
            return np.concatenate(arrays_to_concat)
        return None

    elif isinstance(first_element, list) and len(first_element) > 0 and isinstance(first_element[0], np.ndarray):
        # Special case: list containing numpy arrays
        arrays_to_concat = []
        for item in flattened:
            if isinstance(item, list):
                for sub_item in item:
                    if isinstance(sub_item, np.ndarray):
                        arrays_to_concat.append(sub_item)
        if arrays_to_concat:
            return np.concatenate(arrays_to_concat)
        return None

    else:
        return None

# Example usage:
