import numpy as np

def concatenate_nested(*args):
    if not args:
        return None

    # Get the first element of the first argument to determine the type
    first_element = args[0][0]

    if isinstance(first_element, dict):
        # Case 1: Concatenate dictionaries
        result = {}
        for nested_list in args:
            for item in nested_list:
                if isinstance(item, dict):
                    result.update(item)
        return result

    elif isinstance(first_element, np.ndarray):
        # Case 2: Concatenate numpy arrays
        arrays_to_concat = []
        for nested_list in args:
            for item in nested_list:
                if isinstance(item, np.ndarray):
                    arrays_to_concat.append(item)
        return np.concatenate(arrays_to_concat)

    elif isinstance(first_element, list) and len(first_element) > 0 and isinstance(first_element[0], np.ndarray):
        # Case 3: Concatenate numpy arrays from nested lists
        arrays_to_concat = []
        for nested_list in args:
            for sublist in nested_list:
                if isinstance(sublist, list):
                    for item in sublist:
                        if isinstance(item, np.ndarray):
                            arrays_to_concat.append(item)
        return np.concatenate(arrays_to_concat)

    else:
        raise ValueError("Unsupported input structure for concatenation")

# Example usage:
if __name__ == "__main__":
    # Case 1: Dictionary concatenation
    dict_list1 = [{'a': 1}, {'b': 2}]
    dict_list2 = [{'c': 3}, {'d': 4}]
    print(concatenate_nested(dict_list1, dict_list2))  # Output: {'a': 1, 'b': 2, 'c': 3, 'd': 4}

    # Case 2: Numpy array concatenation
    array_list1 = [np.array([1, 2]), np.array([3, 4])]
    array_list2 = [np.array([5, 6])]
    print(concatenate_nested(array_list1, array_list2))  # Output: [1 2 3 4 5 6]

    # Case 3: Nested list with numpy arrays
    nested_array_list1 = [[np.array([1, 2])], [np.array([3, 4])]]
    nested_array_list2 = [[np.array([5, 6])]]
    print(concatenate_nested(nested_array_list1, nested_array_list2))  # Output: [1 2 3 4 5 6]
