import os
import custom_log as l

def collect_directory_info(path):
    """Collects information about files and folders in the given directory."""
    directory_info = {}
    try:
        for entry in os.scandir(path):
            if entry.is_dir():
                directory_info[entry.name] = collect_directory_info(entry.path)
            elif entry.is_file():
                directory_info[entry.name] = {
                    "size": entry.stat().st_size,
                    "type": entry.name.split('.')[-1] if '.' in entry.name else 'unknown'
                }
    except Exception as e:
        l.error(f"Error accessing {path}: {str(e)}")
    return directory_info

def generate_report(directory_path):
    """Generates a report of the directory structure."""
    report = collect_directory_info(directory_path)
    l.info(f"Directory report for {directory_path}: {report}")

if __name__ == "__main__":
    import folder_walk as walk  # Importing the folder_walk module
    target_directory = walk.get_target_directory()  # Assuming this function gets the target path
    generate_report(target_directory)
```

