Here's a simple and clean C++ implementation of a doubly linked list with methods for adding and deleting nodes while maintaining list integrity:

```cpp
#include <iostream>

class DoublyLinkedList {
private:
    struct Node {
        int data;
        Node* prev;
        Node* next;

        Node(int val = 0) : data(val), prev(nullptr), next(nullptr) {}
    };

    Node* head;  // Dummy head node (empty node as specified)
    int length;

public:
    DoublyLinkedList() : length(0) {
        head = new Node();  // Create empty head node
        head->next = head;  // Circular reference
        head->prev = head;
    }

    ~DoublyLinkedList() {
        Node* current = head->next;
        while (current != head) {
            Node* temp = current;
            current = current->next;
            delete temp;
        }
        delete head;
    }

    // Add node at the end
    void append(int value) {
        Node* newNode = new Node(value);
        Node* tail = head->prev;

        newNode->prev = tail;
        newNode->next = head;
        tail->next = newNode;
        head->prev = newNode;

        length++;
    }

    // Add node at specific position (0-based)
    bool insert(int pos, int value) {
        if (pos < 0 || pos > length) return false;

        Node* newNode = new Node(value);
        Node* current = head;

        // Move to position
        for (int i = 0; i < pos; i++) {
            current = current->next;
        }

        newNode->prev = current;
        newNode->next = current->next;
        current->next->prev = newNode;
        current->next = newNode;

        length++;
        return true;
    }

    // Delete node by position (0-based)
    bool remove(int pos) {
        if (pos < 0 || pos >= length) return false;

        Node* current = head->next;
        for (int i = 0; i < pos; i++) {
            current = current->next;
        }

        current->prev->next = current->next;
        current->next->prev = current->prev;
        delete current;

        length--;
        return true;
    }

    // Print the list
    void display() const {
        Node* current = head->next;
        std::cout << "List: ";
        while (current != head) {
            std::cout << current->data << " ";
            current = current->next;
        }
        std::cout << std::endl;
    }

    int size() const { return length; }
};

int main() {
    DoublyLinkedList list;

    // Create list of length 5
    for (int i = 1; i <= 5; i++) {
        list.append(i);
    }
    list.display();  // List: 1 2 3 4 5

    // Insert at position 2
    list.insert(2, 99);
    list.display();  // List: 1 2 99 3 4 5

    // Remove from position 3
    list.remove(3);
    list.display();  // List: 1 2 99 4 5

    return 0;
}
