class Solution {
    private static Map<Integer, Integer> memo = new HashMap<>();

    public int trifib(int n) {
        // Base cases
        if (n == 0 || n == 1) return 0;
        if (n == 2) return 1;

        // Check if already computed
        if (memo.containsKey(n)) {
            return memo.get(n);
        }

        // Compute and store the result
        int result = trifib(n - 1) + trifib(n - 2) + 2 * trifib(n - 3);
        memo.put(n, result);

        return result;
    }

