    std::string fullName;
    std::string academicID;
    std::vector<double> courseGrades;

public:
    StudentPortfolio(const std::string& name, const std::string& id) 
        : fullName(name), academicID(id) {}

    void addGrade(double grade) {
        if (grade < 0.0 || grade > 4.0) {
            throw std::invalid_argument("Grade must be between 0.0 and 4.0");
        }
        courseGrades.push_back(grade);
    }

    double calculateGPA() const {
        if (courseGrades.empty()) {
            return 0.0;
        }
        return std::accumulate(courseGrades.begin(), courseGrades.end(), 0.0) / courseGrades.size();
    }

    void displayPortfolio() const {
        std::cout << "\nStudent Portfolio Summary\n";
        std::cout << "-------------------------\n";
        std::cout << "Full Name: " << fullName << "\n";
        std::cout << "Academic ID: " << academicID << "\n";
        std::cout << "GPA: " << std::fixed << std::setprecision(2) << calculateGPA() << "\n";
        std::cout << "Course Grades (" << courseGrades.size() << "): ";
        for (double grade : courseGrades) {
            std::cout << grade << " ";
        }
        std::cout << "\n-------------------------\n";
    }

    // Getters
    std::string getFullName() const { return fullName; }
    std::string getAcademicID() const { return academicID; }
};

