        1000000, 900000, 500000, 400000, 100000,
        90000, 50000, 40000, 10000, 9000,
        5000, 4000, 1000, 900, 500, 400,
        100, 90, 50, 40, 10,
        9, 5, 4, 1
    ]
    syms = [
        "M", "CM", "D", "CD", "C", 
        "XC", "L", "XL", "X", "MX",
        "V", "IV", "M", "CM", "D", "CD",
        "C", "XC", "L", "XL", "X",
        "IX", "V", "IV", "I"
    ]

    roman_numeral = ""
    for i in range(len(val)):
        while number >= val[i]:
            roman_numeral += syms[i]
            number -= val[i]

    if not include_subtractive:
        roman_numeral = roman_numeral.replace("IV", "IIII").replace("IX", "VIIII").replace("XL", "XXXX").replace("XC", "LXXXX")

    return format_roman_output(roman_numeral, True)

