    >>> advanced_roman_numeral_conversion('iv', 'roman_to_int', True, True, True) == '(i)v'
    """

    # Define roman numeral mappings
    roman_numerals = {
        1000000: 'M', 900000: 'CM', 500000: 'D', 400000: 'CD',
        100000: 'C', 90000: 'XC', 50000: 'L', 40000: 'XL',
        10000: 'X', 9000: 'MX', 5000: 'V', 4000: 'MV',
        1000: 'M', 900: 'CM', 500: 'D', 400: 'CD',
        100: 'C', 90: 'XC', 50: 'L', 40: 'XL',
        10: 'X', 9: 'IX', 5: 'V', 4: 'IV',
        1: 'I'
    }

    if validate:
        if conversion_type == 'int_to_roman':
            if not (1 <= number <= 1000000):
                raise ValueError("Integer must be between 1 and 1,000,000.")
        elif conversion_type == 'roman_to_int':
            if not isinstance(number, str) or not all(c in "ivxlcdehm" for c in number):
                raise ValueError("Invalid Roman numeral.")

    if conversion_type == 'int_to_roman':
        result = ''
        for value in sorted(roman_numerals.keys(), reverse=True):
            while number >= value:
                result += roman_numerals[value]
                number -= value
                if not include_subtractive and value in [4, 9, 40, 90, 400, 900]:
                    result += roman_numerals[value]  # Add duplicate for non-subtractive

        if format_output:
            result = format_grouped_output(result)
        return result

    elif conversion_type == 'roman_to_int':
        number_map = {v: k for k, v in roman_numerals.items()}
        total = 0
        prev_value = 0

        for char in reversed(number):
            value = number_map[char]
            if value < prev_value:
                total -= value
            else:
                total += value
            prev_value = value

        return total if not format_output else format_grouped_output(number)

def format_grouped_output(roman):
