```python
import json
import os

def squared(num):
    """Returns the square of the provided number."""
    return num * num

def get_user_input():
    """Prompts the user for a number and validates the input."""
    while True:
        user_input = input("Enter a positive number to square (or 'exit' to quit): ")
        if user_input.lower() == 'exit':
            return None
        try:
            num = float(user_input)
            if num <= 0:
                print("Please enter a positive number greater than zero.")
            else:
                return num
        except ValueError:
            print("Invalid input. Please enter a valid number.")

def store_results(results):
    """Stores the results in a JSON file."""
    with open("results.json", "w") as file:
        json.dump(results, file)

def load_results():
    """Loads past results from a JSON file, if it exists."""
    if os.path.exists("results.json"):
        with open("results.json", "r") as file:
            return json.load(file)
    return []

def main():
    past_results = load_results()

    while True:
        num = get_user_input()
        if num is None:
            break

        result = squared(num)
        print(f"The square of {num} is {result}")

        save_choice = input("Do you want to save this result? (yes/no): ").strip().lower()
        if save_choice == 'yes':
            past_results.append(result)
            store_results(past_results)
            print("Result saved.")

        print("Past results:", past_results)

if __name__ == "__main__":
    main()
```

