import java.util.Arrays;
import java.util.List;

public static String[] findAnagrams(String[] lexemes) {
    if (lexemes.length == 0) return lexemes;

    // Get the sorted character array of the first lexeme as reference
    char[] reference = lexemes[0].toCharArray();
    Arrays.sort(reference);
    String referenceKey = new String(reference);

