        char[] current = lexemes[i].toCharArray();
        Arrays.sort(current);
        if (new String(current).equals(referenceKey)) {
            resultantArray.add(lexemes[i]);
        }
    }

    return resultantArray.toArray(new String[resultantArray.size()]);
}
