string match_parens(vector<string> lst) {
    // Function to count net open parentheses
    auto net_balance = [](const string& str) {
        int balance = 0;
        for (char c : str) {
            if (c == '(') {
                balance++;
            } else {
                balance--;
            }
        }
        return balance;
    };

    // Count the balance for both strings
    int balance1 = net_balance(lst[0]);
    int balance2 = net_balance(lst[1]);

    // Check both combinations
    // lst[0] + lst[1] must have non-negative balance through the first string
    // lst[1] + lst[0] must have non-negative balance through the second string
    if (balance1 + balance2 == 0 &&
        (net_balance(lst[0]) >= 0 && balance1 >= -net_balance(lst[1]))) {
        return "Yes";
    }

    if (balance2 + balance1 == 0 &&
        (net_balance(lst[1]) >= 0 && balance2 >= -net_balance(lst[0]))) {
        return "Yes";
    }

    return "No";
}

