    string order1 = lst[0] + lst[1];
    string order2 = lst[1] + lst[0];

    // Function to check if a string has balanced parentheses
    auto is_balanced = [](const string& s) {
        int balance = 0;

        // Iterate through each character
        for (char c : s) {
            if (c == '(') {
                balance++;
            } else {
                balance--;
