public class MainClass {
    public static void main(String[] args) {
        try {
            Object[] designatedSet = {1, 2, 3, 2, 'a', 4, 5, 2, 4, 5, 5}; // Use Object array for mixed types
            showRecurrentEntities(designatedSet);
        } catch (ClassCastException e) {
            System.err.println("Invalid data type encountered: " + e.getMessage());
        }
    }

    static void showRecurrentEntities(Object[] arr) {
        Map<Integer, Integer> counts = new HashMap<>();

        for (Object obj : arr) {
